package class2;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=9;
float x=a;
System.out.println(x);               // automatic type casting

float f=10.20f;
int y=(int)f;
System.out.println(y);              // manual type casting work in opposite direction

	}

}
